var socksjs = require('./socks.js');

var sock = socksjs.connect({host: 'kiwiirc.com', port: '443', ssl: true}, {port: 9150});

sock.on('connect', function () {
	sock.write('GET / HTTP/1.1\r\nHost: kiwiirc.com\r\nConnection: close\r\n\r\n');
});

sock.on('error', console.log);

sock.on('data', function (data) {
	console.log(data.toString());
	//console.log('data');
});

sock.on('end', function () {
	console.log('end');
});
